import { Component } from 'react';
import { ThreeDots } from 'react-loader-spinner';
import Header from '../Header';
import Footer from '../Footer';
import './index.css';

const apiStatusConstants = {
  success: 'SUCCESS',
  failure: 'FAILURE',
  inProgress: 'IN_PROGRESS',
};

class Home extends Component {
  state = {
    apiStatus: apiStatusConstants.inProgress,
    productsList: [],
  };

  componentDidMount() {
    this.getProductsData();
  }

  getProductsData = async () => {
    const url = 'http://localhost:3000/products'; // Backend endpoint for products
    const jwtToken = localStorage.getItem('jwt_token'); // Retrieve JWT token from localStorage
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    };

    try {
      const response = await fetch(url, options);
      if (response.ok) {
        const data = await response.json();
        const formattedData = data.map(product => ({
          id: product.id,
          title: product.title,
          imageUrl: product.imageUrl,
          rating: product.rating,
        }));
        this.setState({
          productsList: formattedData,
          apiStatus: apiStatusConstants.success,
        });
      } else {
        this.setState({ apiStatus: apiStatusConstants.failure });
      }
    } catch (error) {
      console.error('Error fetching products:', error);
      this.setState({ apiStatus: apiStatusConstants.failure });
    }
  };

  renderLoadingView = () => (
    <div className="loader-container" data-testid="loader">
      <ThreeDots height="50" width="50" color="blue" ariaLabel="loading" />
    </div>
  );

  renderFailureView = () => (
    <div className="fail-con">
      <h1 className="fail-h1">Oops! Something Went Wrong</h1>
      <p className="fail-p">
        We cannot seem to find the page you are looking for
      </p>
      <button
        className="retry-btn"
        type="button"
        onClick={this.getProductsData}
      >
        Retry
      </button>
    </div>
  );

  renderProductsList = () => {
    const { productsList } = this.state;

    if (productsList.length === 0) {
      return <p className="no-products">No products found</p>;
    }

    return (
      <ul className="products-list">
        {productsList.map(product => (
          <li key={product.id} className="product-item">
            <img src={product.imageUrl} alt={product.title} className="product-image" />
            <div className="product-rating-con">
              <h1 className="item-title">{product.title}</h1>
              <p className="product-rating">Rating: {product.rating}</p>
            </div>
          </li>
        ))}
      </ul>
    );
  };

  renderProductsView = () => {
    const { apiStatus } = this.state;

    switch (apiStatus) {
      case apiStatusConstants.success:
        return this.renderProductsList();
      case apiStatusConstants.failure:
        return this.renderFailureView();
      case apiStatusConstants.inProgress:
        return this.renderLoadingView();
      default:
        return null;
    }
  };

  render() {
    return (
      <div className="home-container">
        <Header />
        <div className="home-content">
          <h1 className="home-heading">Welcome tohoppy</h1>
          {this.renderProductsView()}
          <p className="home-description">Your one-stop shop for all your needs</p>
        </div>
        <Footer />
      </div>
    );
  }
}

export default Home;
