import {Component} from 'react'
//import {BiSearch} from 'react-icons/bi'
//import { TailSpin } from 'react-loader-spinner';


import './index.css'

const apiStatusConstants = {
  success: 'SUCCESS',
  failure: 'FAILURE',
  inProgress: 'IN_PROGRESS',
}

class Products extends Component {
  state = {
    productStatus: apiStatusConstants.inProgress,
    productsData: [],
    searchInput: '',
    filteredProducts: [],
  }

  componentDidMount() {
    this.getProductsData()
  }

  getProductsData = async () => {
    const url = 'http://localhost:3000/products' // Backend endpoint for products
    const jwtToken = localStorage.getItem('jwt_token') 
    // Retrieve JWT token from localStorage
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    }

    try {
      const response = await fetch(url, options)
      console.log(response)
      if (response.ok) {
        const data = await response.json()
        const formattedData = data.products.map(product => ({
          id: product.id,
          name: product.name,
          description: product.description,
          price: product.price,
          stock: product.stock,// Assuming your backend provides an image URL
        }))
        this.setState({
          productsData: formattedData,
          filteredProducts: formattedData,
          productStatus: apiStatusConstants.success,
        })
      } else {
        this.setState({productStatus: apiStatusConstants.failure})
      }
    } catch (error) {
      console.error('Error fetching products:', error)
      this.setState({productStatus: apiStatusConstants.failure})
    }
  }

  onChangeSearchInput = event => {
    this.setState({searchInput: event.target.value}, this.filterProducts)
  }

  filterProducts = () => {
    const {productsData, searchInput} = this.state
    const filteredProducts = productsData.filter(product =>
      product.name.toLowerCase().includes(searchInput.toLowerCase()),
    )
    this.setState({filteredProducts})
  }

  renderLoadingView = () => (
    <div className="loader-container" data-testid="loader">
     <TailSpin height="50" width="50" color="blue" ariaLabel="loading" />
    </div>
  )

  renderFailureView = () => (
    <div className="fail-con">
      <img
        src="https://assets.ccbp.in/frontend/react-js/failure-img.png"
        className="fail-img"
        alt="failure"
      />
      <h1 className="fail-h1">Oops! Something Went Wrong</h1>
      <p className="fail-p">
        We cannot seem to find the page you are looking for
      </p>
      <button
        className="profile-btn"
        type="button"
        onClick={this.getProductsData}
      >
        Retry
      </button>
    </div>
  )

  renderProductsList = () => {
    const {filteredProducts} = this.state

    if (filteredProducts.length === 0) {
      return <p className="no-products">No products found</p>
    }

    return (
      <ul className="products-list">
        {filteredProducts.map(product => (
          <li key={product.id} className="product-item">
            <h1 className="product-name">{product.name}</h1>
            <p className="product-description">{product.description}</p>
            <p className="product-price">Price: ${product.price}</p>
            <p className="product-stock">Stock: {product.stock}</p>
          </li>
        ))}
      </ul>
    )
  }

  renderProductsView = () => {
    const {productStatus} = this.state

    switch (productStatus) {
      case apiStatusConstants.success:
        return this.renderProductsList()
      case apiStatusConstants.failure:
        return this.renderFailureView()
      case apiStatusConstants.inProgress:
        return this.renderLoadingView()
      default:
        return null
    }
  }

  render() {
    const {searchInput} = this.state
    return (
      <>
        <div className="products-container">
          <div className="search-container">
            <input
              type="search"
              className="search-input"
              placeholder="Search products"
              value={searchInput}
              onChange={this.onChangeSearchInput}
            />
            <button type="button" className="search-btn">
              <BiSearch className="search-icon" />
            </button>
          </div>
          {this.renderProductsView()}
        </div>
      </>
    )
  }
}

export default Products
