import { Component } from 'react';
import CartContext from '../../context/CartContext'; // Import CartContext
import './index.css';

class Login extends Component {
  state = {
    username: '',
    password: '',
    errorMsg: '',
  };

  onChangePassword = event => {
    this.setState({ password: event.target.value });
  };

  onChangeUsername = event => {
    this.setState({ username: event.target.value });
  };

  onSubmit = async (event, addUserToCart) => {
    event.preventDefault();
    const { username, password } = this.state;
    const userData = { username, password };

    const url = 'http://localhost:3000/login/'; // Backend login endpoint

    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    };

    try {
      const req = await fetch(url, options);
      const res = await req.json();

      if (req.ok) {
        const { history } = this.props;
        const jwtToken = res.jwtToken;
        const userId = res.userId;

        // Save JWT and userId to localStorage
        localStorage.setItem('jwt_token', jwtToken);
        localStorage.setItem('user_id', userId);

        // Add user to cart context
        addUserToCart({ userId, username });

        history.replace('/'); // Redirect to home page on success
      } else {
        this.setState({ errorMsg: res.error_msg || 'Login failed. Please try again.' });
      }
    } catch (error) {
      this.setState({ errorMsg: 'Wrong username or password' });
    }
  };

  render() {
    const { username, password, errorMsg } = this.state;

    return (
      <CartContext.Consumer>
        {({ addUserToCart }) => (
          <div className="login-con">
            <div className="login-card">
              <h1 className="HomeLogo">shoppy</h1>
              <form className="form" onSubmit={event => this.onSubmit(event, addUserToCart)}>
                <label className="label" htmlFor="user">
                  USERNAME
                </label>
                <input
                  className="inp"
                  type="text"
                  id="user"
                  onChange={this.onChangeUsername}
                  value={username}
                  placeholder="Enter your username"
                />
                <label className="label" htmlFor="pass">
                  PASSWORD
                </label>
                <input
                  className="inp"
                  type="password"
                  id="pass"
                  onChange={this.onChangePassword}
                  value={password}
                  placeholder="Enter your password"
                />
                <button type="submit" className="login-btn">
                  Login
                </button>
                {errorMsg && <p className="err-msg">{errorMsg}</p>}
              </form>
            </div>
          </div>
        )}
      </CartContext.Consumer>
    );
  }
}

export default Login;
