import React, { useState } from 'react';
import CartContext from '../../context/CartContext';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './index.css';

const Login = () => { // Removed props
  const [state, setState] = useState({
    username: '',
    password: '',
    errorMsg: '',
    isLoading: false,
  });

  const { username, password, errorMsg, isLoading } = state;
  const navigate = useNavigate(); // Initialize useNavigate

  const onChangeUsername = event => {
    setState(prevState => ({ ...prevState, username: event.target.value }));
  };

  const onChangePassword = event => {
    setState(prevState => ({ ...prevState, password: event.target.value }));
  };

  const onSubmit = async event => {
    event.preventDefault();
    setState(prevState => ({ ...prevState, errorMsg: '', isLoading: true }));
    const userData = { username, password };

    const url = 'http://localhost:3000/login/';

    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    };

    try {
      const req = await fetch(url, options);
      const res = await req.json();

      if (req.ok) {
        const jwtToken = res.jwtToken;
        const userId = res.userId;

        localStorage.setItem('jwt_token', jwtToken);
        localStorage.setItem('user_id', userId);

        // Fetch cart list using CartContext
        //getCartList(userId);
        //using context consumer instead.

        navigate('/'); // Redirect to home page
      } else {
        setState({ errorMsg: res.error_msg || 'Login failed. Please try again.', isLoading: false });
      }
    } catch (error) {
        console.error("Login error:", error);
      setState({ errorMsg: 'Network error or invalid credentials.', isLoading: false });
    }
  };

  return (
    <CartContext.Consumer>
      {value => {
        const { getCartList } = value;
        return (
          <div className="login-con">
            <div className="login-card">
              <h1 className="HomeLogo">shoppy</h1>
              <form className="form" onSubmit={onSubmit}>
                <label className="label" htmlFor="user">
                  USERNAME
                </label>
                <input
                  className="inp"
                  type="text"
                  id="user"
                  onChange={onChangeUsername}
                  value={username}
                  placeholder="Enter your username"
                />
                <label className="label" htmlFor="pass">
                  PASSWORD
                </label>
                <input
                  className="inp"
                  type="password"
                  id="pass"
                  onChange={onChangePassword}
                  value={password}
                  placeholder="Enter your password"
                />
                <button type="submit" className="login-btn" disabled={isLoading}>
                  {isLoading ? 'Logging in...' : 'Login'}
                </button>
                {errorMsg && <p className="err-msg">{errorMsg}</p>}
              </form>
            </div>
          </div>
        );
      }}
    </CartContext.Consumer>
  );
};

export default Login;
