import {Component} from 'react'
import {BiSearch} from 'react-icons/bi'
import Loader from 'react-loader-spinner'
import 'react-loader-spinner/dist/loader/css/react-spinner-loader.css'
import Header from '../Header'

import './index.css'

const apiStatusConstants = {
  success: 'SUCCESS',
  failure: 'FAILURE',
  inProgress: 'IN_PROGRESS',
}

class Products extends Component {
  state = {
    profileStatus: apiStatusConstants.inProgress,
    jobStatus: apiStatusConstants.inProgress,
    jobData: [],
    profileData: {},
    searchInput: '',
    jobsList: [],
  }

  componentDidMount() {
    this.getProfileData()
    this.getJobData()
  }

  getProfileData = async () => {
    const url = 'https://apis.ccbp.in/profile'
    const jwtToken = localStorage.getItem('jwt_token')
    const options = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    }
    const req = await fetch(url, options)
    console.log(req)
    const res = await req.json()

    const data = {
      name: res.profile_details.name,
      imageUrl: res.profile_details.profile_image_url,
      shortBio: res.profile_details.short_bio,
    }

    if (req.ok) {
      this.setState({
        profileData: data,
        profileStatus: apiStatusConstants.success,
      })
    } else {
      this.setState({profileStatus: apiStatusConstants.failure})
    }
  }

  getJobData = async () => {
    const url2 = 'https://apis.ccbp.in/jobs'
    const jwtToken = localStorage.getItem('jwt_token')
    const options2 = {
      headers: {
        Authorization: `Bearer ${jwtToken}`,
      },
      method: 'GET',
    }
    const req2 = await fetch(url2, options2)
    const res2 = await req2.json()
    console.log(req2)
    if (req2.ok) {
      const data = res2.jobs.map(i => ({
        id: i.id,
        location: i.location,
        employmentType: i.employment_type,
        rating: i.rating,
        packagePerAnnum: i.package_per_annum,
        title: i.title,
        companyLogoUrl: i.company_logo_url,
        jobDescription: i.job_description,
      }))
      this.setState({
        jobData: [...data],
        jobStatus: apiStatusConstants.success,
        jobsList: [...data],
      })
    } else {
      this.setState({jobStatus: apiStatusConstants.failure})
    }
  }

  onChangeSearchInput = event => {
    this.setState({searchInput: event.target.value})
  }

  filterData = () => {
    const {jobData, searchInput} = this.state
    const k = jobData.filter(i =>
      i.title.toLowerCase().includes(searchInput.toLowerCase()),
    )
    console.log(k)
    if (k.length === 0) {
      this.setState({jobStatus: apiStatusConstants.failure, searchInput: ''})
    } else {
      this.setState({jobsList: [...k], jobStatus: apiStatusConstants.success})
    }
  }

  salaryFilter = s => {
    const {jobData} = this.state
    const k = jobData.filter(
      i =>
        parseInt(i.packagePerAnnum.substring(0, 2)) >=
        parseInt(s.substring(0, 2)),
    )
    this.setState({jobsList: [...k]})
  }

  jobFilter = (check, label) => {
    const {jobData, jobsList} = this.state

    if (check === true) {
      if (jobsList.length < 60) {
        const k = jobData.filter(
          i => i.employmentType.toLowerCase() === label.toLowerCase(),
        )
        this.setState(prevState => ({jobsList: [...k, ...prevState.jobsList]}))
      } else {
        const k = jobData.filter(
          i => i.employmentType.toLowerCase() === label.toLowerCase(),
        )
        this.setState({jobsList: [...k]})
      }
    } else {
      const k = jobsList.filter(
        i => i.employmentType.toLowerCase() !== label.toLowerCase(),
      )
      this.setState({jobsList: [...k]})
    }
  }

  renderLoadingView = () => (
    <div className="loader-container" data-testid="loader">
      <Loader type="ThreeDots" color="#0b69ff" height="50" width="50" />
    </div>
  )

  onGetRadioButtonsView = () => (
    <ul className="filter-con">
      {salaryRangesList.map(eachItem => (
        <li className="li-container" key={eachItem.salaryRangeId}>
          <input
            className="radio"
            id={eachItem.salaryRangeId}
            type="radio"
            name="option"
            onChange={() => this.salaryFilter(eachItem.salaryRangeId)}
          />
          <label className="label" htmlFor={eachItem.salaryRangeId}>
            {eachItem.label}
          </label>
        </li>
      ))}
    </ul>
  )

  onGetCheckBoxesView = () => (
    <ul className="filter-con">
      {employmentTypesList.map(eachItem => (
        <li className="li-container" key={eachItem.employmentTypeId}>
          <input
            className="input"
            id={eachItem.employmentTypeId}
            type="checkbox"
            onChange={event =>
              this.jobFilter(event.target.checked, eachItem.label)
            }
          />
          <label className="label" htmlFor={eachItem.employmentTypeId}>
            {eachItem.label}
          </label>
        </li>
      ))}
    </ul>
  )

  onProfileSuccessView = () => {
    const {profileData} = this.state
    return (
      <div>
        <img src={profileData.imageUrl} className="profile-img" alt="profile" />
        <h1 className="profile-h1">{profileData.name}</h1>
        <p className="profile-p">{profileData.shortBio}</p>
      </div>
    )
  }

  onProfileFailureView = () => (
    <button
      className="profile-btn"
      type="button"
      onClick={() => this.componentDidMount()}
    >
      Retry
    </button>
  )

  onGetJobsView = () => {
    const {jobsList, jobData} = this.state

    if (jobsList.length <= 0) {
      this.setState({jobsList: jobData})
    }
    console.log(jobsList)

    return (
      <ul className="succ-con">
        {jobsList.map(i => (
          <JobItem item={i} key={i.id} />
        ))}
      </ul>
    )
  }

  onGetJobsFailureView = () => (
    <div className="fail-con">
      <img
        src="https://assets.ccbp.in/frontend/react-js/failure-img.png"
        className="fail-img"
        alt="failure"
      />
      <h1 className="fail-h1">Oops! Something Went Wrong</h1>
      <p className="fail-p">
        We cannot seem to find the page you are looking for
      </p>
      <button
        className="profile-btn"
        type="button"
        onClick={() => this.componentDidMount()}
      >
        Retry
      </button>
    </div>
  )

  onRenderJobsStatus = () => {
    const {jobStatus} = this.state

    switch (jobStatus) {
      case apiStatusConstants.success:
        return this.onGetJobsView()
      case apiStatusConstants.failure:
        return this.onGetJobsFailureView()
      case apiStatusConstants.inProgress:
        return this.renderLoadingView()
      default:
        return null
    }
  }

  onRenderProfileStatus = () => {
    const {profileStatus} = this.state

    switch (profileStatus) {
      case apiStatusConstants.success:
        return this.onProfileSuccessView()
      case apiStatusConstants.failure:
        return this.onProfileFailureView()
      case apiStatusConstants.inProgress:
        return this.renderLoadingView()
      default:
        return null
    }
  }

  render() {
    const {searchInput} = this.state
    return (
      <>
        <Header />
        <div className="jobs-con">
          <div className="left-con">
            <div className="profile-con">{this.onRenderProfileStatus()}</div>

            <hr className="hr" />

            <h1 className="checkbox-h1">Type of Employment</h1>

            {this.onGetCheckBoxesView()}
            <br />
            <hr className="hr" />

            <h1 className="checkbox-h1">Salary range</h1>

            {this.onGetRadioButtonsView()}
          </div>
          <div className="right-con">
            <div className="inp-con">
              <input
                type="search"
                className="inp2"
                placeholder="search"
                value={searchInput}
                onChange={this.onChangeSearchInput}
                onKeyDown={event => {
                  if (event.key === 'Enter') {
                    this.filterData()
                  }
                }}
              />
              <button
                type="button"
                onClick={this.filterData}
                className="search-btn"
              >
                <BiSearch className="search-logo" />
              </button>
            </div>
            {this.onRenderJobsStatus()}
          </div>
        </div>
      </>
    )
  }
}

export default Products
