import { Component } from 'react';
import { Redirect } from 'react-router-dom';
import './index.css';

class Login extends Component {
  state = {
    username: '',
    password: '',
    errorMsg: '',
    showSubmitError: false,
  };

  onChangePassword = event => {
    this.setState({ password: event.target.value });
  };

  onChangeUsername = event => {
    this.setState({ username: event.target.value });
  };

  onSubmitSuccess = jwtToken => {
    const { history } = this.props;
    localStorage.setItem('jwt_token', jwtToken); // Save JWT token to localStorage
    history.replace('/products'); // Redirect to '/products' on success
  };

  onSubmitFailure = errorMsg => {
    this.setState({ showSubmitError: true, errorMsg }); // Show error message on failure
  };

  onSubmit = async event => {
    event.preventDefault();
    const { username, password } = this.state;
    const userData = { username, password };

    const url = 'http://localhost:3000/login/';
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    };

    try {
      const req = await fetch(url, options);
      const res = await req.json();

      if (req.ok) {
        this.onSubmitSuccess(res.jwtToken); // Handle success
      } else {
        this.onSubmitFailure(res.error_msg || 'Login failed. Please try again.'); // Handle failure
      }
    } catch (error) {
      this.onSubmitFailure('Network error. Please try again later.');
    }
  };

  render() {
    const { username, password, errorMsg, showSubmitError } = this.state;
    const jwtToken = localStorage.getItem('jwt_token');

    if (jwtToken !== null) {
      return <Redirect to="/products" />;
    }

    return (
      <div className="login-con">
        <div className="login-card">
          <img
            src="https://assets.ccbp.in/frontend/react-js/logo-img.png"
            className="card-logo"
            alt="logo"
          />
          <form className="form" onSubmit={this.onSubmit}>
            <label className="label" htmlFor="user">
              USERNAME
            </label>
            <input
              className="inp"
              type="text"
              id="user"
              onChange={this.onChangeUsername}
              value={username}
              placeholder="Enter your username"
            />
            <label className="label" htmlFor="pass">
              PASSWORD
            </label>
            <input
              className="inp"
              type="password"
              id="pass"
              onChange={this.onChangePassword}
              value={password}
              placeholder="Enter your password"
            />
            <button type="submit" className="login-btn">
              Login
            </button>
            {showSubmitError && <p className="err-msg">*{errorMsg}</p>}
          </form>
        </div>
      </div>
    );
  }
}

export default Login;
